use PHPMailer\PHPMailer\PHPMailer;
use PHPMailer\PHPMailer\Exception;

if (!class_exists('PHPMailer\PHPMailer\PHPMailer')) {
    require_once ABSPATH . WPINC . '/PHPMailer/PHPMailer.php';
    require_once ABSPATH . WPINC . '/PHPMailer/SMTP.php';
    require_once ABSPATH . WPINC . '/PHPMailer/Exception.php';
}

function simple_email_form() {
    $form_url = ABSPATH . '../../themes/pampa54/pageContact.php';

    if (file_exists($form_url)) {
        return file_get_contents($form_url);
    } else {
        return '<p>Error: No se pudo cargar el formulario. Verifica la ruta.</p>';
    }
}

add_shortcode('simple_email_form', 'simple_email_form');

